pragma solidity ^0.4.19;

import "zeppelin-solidity/contracts/ownership/Ownable.sol";
import "zeppelin-solidity/contracts/lifecycle/Pausable.sol";
import "zeppelin-solidity/contracts/math/SafeMath.sol";
import "zeppelin-solidity/contracts/token/ERC20/ERC20.sol";

contract Collector is Ownable, Pausable {
    using SafeMath for uint256;

    uint constant public MAX_INVESTOR_COUNT = 50;

    uint constant public PERCENT_DELIMITER = 10 ** 3;

    event InvestorAddition(address indexed investor, uint commission);
    event InvestorRemoval(address indexed investor);

    // PERCENT_DELIMITER = 1%
    mapping (address => uint) public investorCommissions;
    address[] public investors;

    ERC20 public token;

    modifier investorDoesNotExist(address _investor) {
        require(investorCommissions[_investor] == 0);
        _;
    }

    modifier investorExists(address _investor) {
        require(investorCommissions[_investor] > 0);
        _;
    }

    modifier notNull(address _address) {
        require(_address != 0);
        _;
    }

    modifier validRequirement(uint _investorCount) {
        require (_investorCount <= MAX_INVESTOR_COUNT && _investorCount != 0);
        _;
    }

    function Collector(ERC20 _token, address[] _investors, uint[] _commissions)
    public
    validRequirement(_investors.length) {
        require(_token != address(0));
        require(_investors.length == _commissions.length);

        token = _token;

        for (uint i=0; i < _investors.length; i++) {
            require (investorCommissions[_investors[i]] == 0 && _investors[i] != 0);
            investorCommissions[_investors[i]] = _commissions[i];
            InvestorAddition(_investors[i], _commissions[i]);
        }
        investors = _investors;
    }

    // need to receive money from trader
    function () external payable { }

    function addInvestor(address _investor, uint _commission)
    public
    onlyOwner
    investorDoesNotExist(_investor)
    notNull(_investor)
    validRequirement(investors.length + 1) {
        investorCommissions[_investor] = _commission;
        investors.push(_investor);
        InvestorAddition(_investor, _commission);
    }

    function removeInvestor(address _investor)
    public
    onlyOwner
    investorExists(_investor) {
        delete investorCommissions[_investor];
        for (uint i=0; i<investors.length - 1; i++) {
            if (investors[i] == _investor) {
                investors[i] = investors[investors.length - 1];
                break;
            }
        }
        investors.length -= 1;
        InvestorRemoval(_investor);
    }

    function getInvestors()
    public
    view
    returns (address[]) {
        return investors;
    }

    function withdrawCommission()
    onlyOwner
    public {
        uint tokenBalance = token.balanceOf(address(this));
        uint weiBalance = this.balance;
        require(weiBalance > 0 || tokenBalance > 0);

        for (uint i = 0; i < investors.length; i++) {
            address investor = investors[i];
            uint commission = investorCommissions[investor];

            if(weiBalance > 0) {
                uint weiValue = weiBalance.mul(commission).div(100).div(PERCENT_DELIMITER);
                investor.transfer(weiValue);
            }

            if(tokenBalance > 0) {
                uint tokenValue = tokenBalance.mul(commission).div(100).div(PERCENT_DELIMITER);
                token.transfer(investor, tokenValue);
            }
        }
    }
}
