pragma solidity ^0.4.19;

import "zeppelin-solidity/contracts/ownership/Ownable.sol";
import "zeppelin-solidity/contracts/token/ERC20/MintableToken.sol";
import "./tokens/AirDropToken.sol";

contract Issuer is Ownable {
    address public trader;
    ERC20Basic public token;
    uint private initialSupply;

    function Issuer(ERC20Basic _token, uint _initialSupply) public {
        require(_token != address(0));
        token = _token;

        initialSupply = _initialSupply;
    }

    function setTrader(address _trader) external {
        require(_trader != address(0));
        trader = _trader;
    }

    function withdrawTokenOwnership(address _newOwner) external onlyOwner {
        require(_newOwner != address(0));
        Ownable(token).transferOwnership(_newOwner);
    }

    function initIssue() public onlyOwner {
        MintableToken(token).mint(address(this), initialSupply);
    }

    function issue(uint _value) public onlyOwner {
        require(_value > 0);
        MintableToken(token).mint(address(this), _value);
    }

    function trade(uint _value) public onlyOwner {
        require(_value > 0);
        uint balance = token.balanceOf(address(this));
        require(_value <= balance);

        token.transfer(trader, _value);
    }

    function drop(address[] _participants, uint _value) external onlyOwner {
        require(_participants.length > 0);
        require(_value > 0);

        AirDropToken(token).drop(_participants, _value);
    }
}
